import axios from 'axios';

const API_URL = process.env.REACT_APP_API_BASE_URL;

// Login function
const login = async (email, password) => {
    try {
        // Prepare key-value pairs for x-www-form-urlencoded
        const formData = new URLSearchParams();
        formData.append('username', email); // 'username' key with the email value
        formData.append('password', password); // 'password' key

        // Send the POST request
        const response = await axios.post(`${API_URL}/auth/login`, formData, {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded', // Ensure content type is set
            },
        });

        // Save the JWT token in local storage
        localStorage.setItem('token', response.data.token);

        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Login failed');
    }
};

// Logout function
const logout = () => {
    localStorage.removeItem('token'); // Remove the token from local storage
};

// Get the currently logged-in user's token
const getToken = () => {
    return localStorage.getItem('token');
};

// Check if the user is authenticated
const isAuthenticated = () => {
    const token = getToken();
    return token !== null; // Return true if token exists, false otherwise
};

// Attach Authorization header with the token for protected API calls
const setAuthHeader = () => {
    const token = getToken();
    if (token) {
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
        delete axios.defaults.headers.common['Authorization'];
    }
};

const AuthService = {
    login,
    logout,
    getToken,
    isAuthenticated,
    setAuthHeader,
};

export default AuthService;
