import React, { useState } from 'react';
import CourseService from '../../services/CourseService';

const CourseForm = () => {
    const [form, setForm] = useState({
        courseCode: '',
        courseName: '',
        courseDescription: '',
        courseYear: '',
        courseCredits: '',
        courseTerm: '',
        courseCapacity: '',
        employeeId: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const requestBody = {
            courseCode: form.courseCode,
            courseName: form.courseName,
            courseDescription: form.courseDescription,
            courseYear: form.courseYear,
            courseCredits: form.courseCredits,
            courseTerm: form.courseTerm,
            courseCapacity: form.courseCapacity,
            employee: {
                employeeId: form.employeeId,
            },
        };
        try {
            await CourseService.addCourse(requestBody);
            alert('Course added successfully!');
            setForm({
                courseCode: '',
                courseName: '',
                courseDescription: '',
                courseYear: '',
                courseCredits: '',
                courseTerm: '',
                courseCapacity: '',
                employeeId: '',
            });
        } catch (error) {
            console.error(error);
            alert('Failed to add course.');
        }
    };

    return (
        <div
            className="d-flex justify-content-center align-items-center"
            style={{
                background: 'linear-gradient(135deg, #0077b6, #0096c7)',
            }}
        >
            <div>
                <h2
                    className="text-center mb-4"
                    style={{
                        fontWeight: '700',
                        color: '#023e8a',
                    }}
                >
                    Add a New Course
                </h2>
                <form onSubmit={handleSubmit}>
                    <div className="row">
                        <div className="col-md-6 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Course Code
                            </label>
                            <input
                                type="text"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseCode"
                                value={form.courseCode}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Course Name
                            </label>
                            <input
                                type="text"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseName"
                                value={form.courseName}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="col-md-12 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Description
                            </label>
                            <textarea
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseDescription"
                                value={form.courseDescription}
                                onChange={handleInputChange}
                            ></textarea>
                        </div>
                        <div className="col-md-4 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Year
                            </label>
                            <input
                                type="number"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseYear"
                                value={form.courseYear}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="col-md-4 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Credits
                            </label>
                            <input
                                type="number"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseCredits"
                                value={form.courseCredits}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="col-md-4 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Term
                            </label>
                            <input
                                type="number"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseTerm"
                                value={form.courseTerm}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Capacity
                            </label>
                            <input
                                type="number"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="courseCapacity"
                                value={form.courseCapacity}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 mb-3">
                            <label
                                style={{
                                    fontSize: '16px',
                                    fontWeight: '500',
                                    color: '#495057',
                                }}
                            >
                                Employee ID
                            </label>
                            <input
                                type="number"
                                className="form-control"
                                style={{
                                    border: '1px solid #adb5bd',
                                    borderRadius: '8px',
                                    padding: '12px 15px',
                                    fontSize: '15px',
                                }}
                                name="employeeId"
                                value={form.employeeId}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                    </div>
                    <button
                        type="submit"
                        className="btn w-100"
                        style={{
                            backgroundColor: '#0077b6',
                            color: '#ffffff',
                            fontWeight: '600',
                            borderRadius: '8px',
                            fontSize: '16px',
                            padding: '12px 15px',
                            border: 'none',
                        }}
                    >
                        Add Course
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CourseForm;
