import React, { useEffect, useState } from "react";
import { getSchedules } from "../services/ScheduleService"; // Fetch schedules
import { getCourses } from "../services/CourseService"; // Fetch courses

const ScheduleList = () => {
  const [schedules, setSchedules] = useState([]);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const scheduleData = await getSchedules(); // Fetch schedules
        const courseData = await getCourses(); // Fetch courses
        setSchedules(scheduleData);
        setCourses(courseData);
      } catch (error) {
        console.error("Error fetching schedules or courses:", error);
      }
    };
    fetchData();
  }, []);

  const getCourseName = (courseId) => {
    const course = courses.find((course) => course.courseId === courseId);
    return course ? course.courseName : "Unknown Course";
  };

  return (
    <div className="container mt-4">
      <h3>Course Schedules</h3>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Schedule ID</th>
            <th>Course</th>
            <th>Day</th>
            <th>Time</th>
            <th>Room</th>
            <th>Building</th>
          </tr>
        </thead>
        <tbody>
          {schedules.map((schedule) => (
            <tr key={schedule.courseScheduleId}>
              <td>{schedule.courseScheduleId}</td>
              <td>{getCourseName(schedule.courseId)}</td>
              <td>{schedule.courseDay}</td>
              <td>{schedule.courseTime}</td>
              <td>{schedule.room}</td>
              <td>{schedule.building || "N/A"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ScheduleList;
