import axios from 'axios';
import { useNavigate } from 'react-router-dom';
axios.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token'); // Retrieve token from localStorage
        if (token) {
            config.headers['Authorization'] = `Bearer ${token}`; // Add token to Authorization header
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

const API_URL = process.env.REACT_APP_API_BASE_URL;

// Login function
const login = async (email, password) => {
    try {
        // Prepare data as key-value pairs
        const formData = new URLSearchParams();
        formData.append('username', email);
        formData.append('password', password);
       

        // Send POST request with x-www-form-urlencoded format
        const response = await axios.post(`/auth/login`, formData, {
            headers: {
                
                'Content-Type': 'application/x-www-form-urlencoded', // Specify content type
            },
        });


        const rawToken = response.data; // Full token, e.g., "Bearer eyJhbGci..."
        const jwtToken = rawToken.split(' ')[1]; // Extract the JWT part

        // Log the raw token and the extracted JWT
        console.log('Full Token:', rawToken);
        console.log('JWT Token:', jwtToken);
        axios.defaults.headers.common['Authorization'] = rawToken;
        // Save only the JWT in local storage
        localStorage.setItem('token', jwtToken);
        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Login failed');
    }
};


//Expired Function
const isTokenExpired = () => {
    const token = getToken(); // Retrieve the token using getToken function
    if (!token) return true; // If no token exists, treat it as expired

    try {
        // Decode the token payload
        const [, payload] = token.split('.'); // Split the token to get the payload part
        const decodedPayload = JSON.parse(atob(payload)); // Decode the base64 payload
        const now = Math.floor(Date.now() / 1000); // Current time in seconds
        console.log(decodedPayload.exp);
        // Check if the current time is greater than the expiration time
        return decodedPayload.exp < now; // true if expired, false otherwise
    } catch (error) {
        console.error('Error decoding token:', error);
        return true; // If there's an error decoding, assume the token is invalid/expired
    }
};


// Logout function
const logout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    useNavigate.navigate('/');
};

// Get the currently logged-in user's token
const getToken = () => {
    return localStorage.getItem('token');
};

// Check if the user is authenticated
const isAuthenticated = () => {
    const token = getToken();
    const result =(token !== null);
    return result;
};

// Attach Authorization header with the token for protected API calls
const setAuthHeader = () => {
    const token = getToken();
    console.log('auth header', 'token');
    if (token) {
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
        delete axios.defaults.headers.common['Authorization'];
    }
};

const AuthService = {
    login,
    logout,
    getToken,
    isAuthenticated,
    setAuthHeader,
    isTokenExpired
};

export default AuthService;
