import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthService from '../services/AuthService';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            await AuthService.login(email, password);
            navigate('/dashboard'); // Redirect to employees page
        } catch (error) {
            alert('Invalid credentials.');
        }
    };

    return (
        <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
            <div
                className="card p-5 shadow-lg border-0"
                style={{ width: '380px', borderRadius: '10px', backgroundColor: '#f8f9fa' }}
            >
                <h3 className="text-center mb-4" style={{ fontWeight: '600', color: '#343a40' }}>
                    Welcome Back
                </h3>
                <p className="text-center text-muted mb-4">
                    Enter your credentials to access your account.
                </p>
                <form onSubmit={handleLogin}>
                    <div className="mb-4">
                        <label className="form-label" style={{ fontWeight: '500' }}>Email</label>
                        <input
                            type="email"
                            className="form-control form-control-lg"
                            style={{
                                border: '1px solid #ced4da',
                                borderRadius: '8px',
                                padding: '10px 15px',
                            }}
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Enter your email"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="form-label" style={{ fontWeight: '500' }}>Password</label>
                        <input
                            type="password"
                            className="form-control form-control-lg"
                            style={{
                                border: '1px solid #ced4da',
                                borderRadius: '8px',
                                padding: '10px 15px',
                            }}
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                            required
                        />
                    </div>
                    <button
                        type="submit"
                        className="btn btn-primary btn-lg w-100"
                        style={{
                            backgroundColor: '#0d6efd',
                            borderColor: '#0d6efd',
                            borderRadius: '8px',
                            fontWeight: '600',
                        }}
                    >
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default LoginPage;
