import React, { useState, useEffect } from "react";
import ScheduleService  from "../../services/ScheduleService"; // Fetch schedules
import CourseService from "../../services/CourseService"; // Fetch courses

const ScheduleForm = ({ onSuccess }) => {
  const [schedule, setSchedule] = useState({
    courseId: "",
    courseDay: "",
    courseTime: "",
    room: "",
    building: "",
  });

  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const courseData = await CourseService.getAllCourses(); // Fetch courses
        setCourses(courseData);
        console.log(courseData);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };
    fetchCourses();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSchedule((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await ScheduleService.addSchedule(schedule); // Save schedule through the service
      onSuccess(); // Callback to notify parent of success
    } catch (error) {
      console.error("Error saving schedule:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h3>Add/Update Schedule</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="courseId">Course</label>
          <select
            id="courseId"
            name="courseId"
            className="form-control"
            value={schedule.courseId}
            onChange={handleChange}
            required
          >
            <option value="">Select a Course</option>
            {courses.map((course) => (
              <option key={course.courseId} value={course.courseId}>
                {course.courseName}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="courseDay">Day</label>
          <input
            type="text"
            id="courseDay"
            name="courseDay"
            className="form-control"
            value={schedule.courseDay}
            onChange={handleChange}
            placeholder="e.g., Monday"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="courseTime">Time</label>
          <input
            type="text"
            id="courseTime"
            name="courseTime"
            className="form-control"
            value={schedule.courseTime}
            onChange={handleChange}
            placeholder="e.g., 10:00 AM - 12:00 PM"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="room">Room</label>
          <input
            type="text"
            id="room"
            name="room"
            className="form-control"
            value={schedule.room}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="building">Building</label>
          <input
            type="text"
            id="building"
            name="building"
            className="form-control"
            value={schedule.building}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="btn btn-primary mt-3">
          Save Schedule
        </button>
      </form>
    </div>
  );
};

export default ScheduleForm;
